import React, { useEffect, useState } from "react";

function App() {
  const [media, setMedia] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8080/api/media")
      .then(res => res.json())
      .then(data => setMedia(data.content || []))
      .catch(err => console.error(err));
  }, []);

  return (
    <div style={{ padding: 20 }}>
      <h1>Media Scraper</h1>

      <div style={{ display: "grid", gridTemplateColumns: "repeat(3, 1fr)", gap: 10 }}>
        {media.map(item => (
          <div key={item.id}>
            {item.type === "IMAGE" ? (
              <img src={item.mediaUrl} alt="" style={{ width: "100%" }} />
            ) : (
              <video src={item.mediaUrl} controls style={{ width: "100%" }} />
            )}
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
